import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductCard from './ProductCard';
import RegisterUser from './RegisterUser';

const AdminPanel = () => {
  const [products, setProducts] = useState([]);
  const [users, setUsers] = useState([]);

  const [loading, setLoading] = useState(true); // Added loading state
  const [error, setError] = useState(''); // Added error state
  
  useEffect(() => {
    const fetchProducts = async () => {
      const { data } = await axios.get('http://localhost:5000/api/products');
      setProducts(data);
    };

    const fetchUsers = async () => {
        try {
          const { data } = await axios.get('http://localhost:5000/api/users/');
          setUsers(data);
        } catch (error) {
          console.error('Error fetching users:', error);
          setError('Failed to fetch users');
        } finally {
          setLoading(false); // Set loading to false after data fetch attempt
        }
      };
  
      fetchProducts();
      fetchUsers();  // <-- Fetch users when the component mounts
    }, []);
  
    const handleRemoveUser = async (userId) => {  // <-- New function to handle user removal
      try {
        await axios.delete(`http://localhost:5000/api/users/${userId}`);
        setUsers(users.filter(user => user._id !== userId));  // <-- Update state to reflect removed user
      } catch (error) {
        console.error('Failed to remove user:', error);
      }
  };

  if (loading) return <p>Loading...</p>; // Show loading indicator
  if (error) return <p>{error}</p>; // Show error message

  return (
    <div>
      <h1>Admin Panel</h1>
      <RegisterUser />
      {products.map((product) => (
        <ProductCard key={product._id} product={product} role="admin" />
      ))}
      <h2>Users</h2> 
      <ul>
        {users.map((user) => (
          <li key={user._id}>
            {user.username} - {user.role}
            <button onClick={() => handleRemoveUser(user._id)}>Remove</button>  
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPanel;
