import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductCard from './ProductCard';

const StockManagerPanel = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const { data } = await axios.get('http://localhost:5000/api/products');
      setProducts(data);
    };
    fetchProducts();
  }, []);

//   const handleUpdate = async (productId, updatedFields) => {
//     try {
//     const token = localStorage.getItem('token');
//     console.log(token)
//     const config =  { headers: {
//         'Authorization': `Bearer ${token}`,
//       },
//     };
//       const { data: updatedProduct } = await axios.put(`http://localhost:5000/api/products/${productId}`, updatedFields, config);
//       setProducts(products.map(p => (p._id === productId ? updatedProduct : p)));
//     } catch (error) {
//       console.error("Failed to update the product:", error);
//     }
//   };


const handleUpdate = async (productId, updatedFields) => {
    try {
      const token = localStorage.getItem('authToken');
      console.log('Retrieved Token:', token);

      if (!token) {
        throw new Error("No token found");
      }

      const config = {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      };

      const { data: updatedProduct } = await axios.put(
        `http://localhost:5000/api/products/${productId}`, 
        updatedFields, 
        config
      );

      // Update the local state with the updated product
      setProducts(products.map(p => (p._id === productId ? updatedProduct : p)));
    } catch (error) {
      console.error("Failed to update the product:", error.response ? error.response.data : error.message);
    }
  };

  return (
    <div>
      <h1>Stock Manager Panel</h1>
      {products.map((product) => (
        <ProductCard
          key={product._id}
          product={product}
          role="stockManager"
          onUpdate={handleUpdate}
        />
      ))}
    </div>
  );
};

export default StockManagerPanel;
