import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductCard from './ProductCard';

const StockManagerPanel = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const { data } = await axios.get('http://localhost:5000/api/products');
      setProducts(data);
    };
    fetchProducts();
  }, []);

  const handleUpdate = async (productId, updatedFields) => {
    try {
    const token = localStorage.getItem('token')
      const { data: updatedProduct } = await axios.put(`http://localhost:5000/api/products/${productId}`,{
        headers: {
            Authorization: `Bearer ${token}`
        }
    }, updatedFields);
      setProducts(products.map(p => (p._id === productId ? updatedProduct : p)));
    } catch (error) {
      console.error("Failed to update the product:", error);
    }
  };

  return (
    <div>
      <h1>Stock Manager Panel</h1>
      {products.map((product) => (
        <ProductCard
          key={product._id}
          product={product}
          role="stockManager"
          onUpdate={handleUpdate}
        />
      ))}
    </div>
  );
};

export default StockManagerPanel;
